import { HttpEvent, HttpHandler, HttpInterceptor, HttpRequest } from "@angular/common/http";
import { Injectable } from "@angular/core";
import { Observable, switchMap } from "rxjs";

@Injectable()
export class AuthInterceptor implements HttpInterceptor {
    intercept(req: HttpRequest<any>, next: HttpHandler): Observable<HttpEvent<any>> {
        const token = localStorage.getItem('token');

        if (token) {
            // Token found, attach to request
            return this.attachTokenAndProceed(req, next, token);
        } else {
            // Token not found, try retrieving before proceeding
            return this.fetchTokenAndProceed(req, next);
        }
    }

    private attachTokenAndProceed(req: HttpRequest<any>, next: HttpHandler, token: string): Observable<HttpEvent<any>> {
        const authReq = req.clone({
            setHeaders: { Authorization: `Bearer ${token}` }
        });
        return next.handle(authReq);
    }

    private fetchTokenAndProceed(req: HttpRequest<any>, next: HttpHandler): Observable<HttpEvent<any>> {
        // Call a service to fetch the token from the API
        return this.userService.fetchToken()
            .pipe(
                switchMap((token: any) => {
                    localStorage.setItem('token', token);
                    return this.attachTokenAndProceed(req, next, token);
                }),
                catchError((error: any) => {
                    // Handle token retrieval errors (e.g., redirect to login)
                    return this.handleUnauthorizedRequest(next, req);
                })
            );
    }

    private handleUnauthorizedRequest(next: HttpHandler, req: HttpRequest<any>): Observable<HttpEvent<any>> {
        // Handle unauthorized requests (e.g., redirect to login)
        return next.handle(req);
    }
}
