import { HttpClient, HttpErrorResponse, HttpHeaders } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { BehaviorSubject, Observable, catchError, map, tap } from 'rxjs';
import { environment } from 'src/environments/environment';

@Injectable({
  providedIn: 'root'
})
export class UserService {


  private jwtTokenSubject = new BehaviorSubject<string | null>(null);
  jwtToken$ = this.jwtTokenSubject.asObservable();

  constructor(private http: HttpClient) {
    // Call the login method during service initialization
    this.login().subscribe(
      () => {
        console.log('Login successful');
      },
      (error) => {
        console.error('Login failed:', error);
      }
    );
  }

  login(): Observable<any> {
    // Simulate a login process and obtain a JWT token from the server
    return this.http.get<any>(`${environment.apiEndpoint}/token`).pipe(
      tap((response) => {
        const token = response.token;
        this.setToken(token);
      })
    );
  }

  isAuthenticated(): Observable<boolean> {
    // Check if the user is authenticated (e.g., check if a token is available)
    return this.jwtToken$.pipe(map((token) => !!token));
  }

  setToken(token: string): void {
    this.jwtTokenSubject.next(token);
  }

}
