import { HttpClient } from '@angular/common/http';
import { Component } from '@angular/core';
import { environment } from 'src/environments/environment';

@Component({
  selector: 'app-search',
  templateUrl: './search.component.html',
  styleUrls: ['./search.component.scss']
})
export class SearchComponent {

  query: string = '';
  searchRes: any;

  constructor(private http: HttpClient) {

  }

  searchRepos() {
    this.http.get(`${environment.apiEndpoint}/search?query=${this.query}`).subscribe((e: any) => {
      this.searchRes = e;
    }, (e) => console.log(e));

  }

  bookMark(repo: any) {
    this.http.post(`${environment.apiEndpoint}/bookmark`, repo).subscribe((e: any) => {


    }, e => console.log(e));
  }

}
<mat-slide-toggle>Toggle me!</mat-slide-toggle>
<div>
    <input [(ngModel)]="query" placeholder="Enter repo name">
    <button (click)="searchRepos()">Search</button>
</div>

<div *ngIf="searchRes">
    <div *ngFor="let item of searchRes.items ">
        <div>{{item.name}}</div>
        <div>{{item.owner.login}}</div>
        <img [src]="item.owner.avatar_url"/>
        <button (click)="bookMark(item)">Bookmark</button>
    </div>
</div>
